import React from "react";
import {graphql} from "react-apollo";
import { I18n } from "react-redux-i18n";
import Loading from "../../../core/ui/components/loading";
import PostCreate from "../components/post-create";
import {createContainer} from "meteor/react-meteor-data";
import gql from "graphql-tag";
import notie from "notie";

class PostCreateContainer extends React.Component {
    constructor() {
        super();

        this.savePosts=this.savePosts.bind(this);
    }
    savePosts(data){
        let getTranslation = (key) => {
            return "Agenda.publish.modal.messages." + key;
        };
        let component=this;
        //TODO: Validate Data
        Meteor.call("posts-scheduler",data,function(err,res){
            if(err){
                if(err.error===500){
                    err.error = "Failed to create post.";
                }
                console.error("ERROR_SAVING_POST",err);
                notie.alert(3,err.reason||err.error,3);
                return
            }
            notie.alert(1,I18n.t(getTranslation("saved")),3);
            component.props.reloadAgenda();
            component.props.close();
            //console.log("SERVER_SAVE_POSTS_RESPONSE",res);
        });
    }
    render() {
        return (
            <div>
                {this.props.loading ? <Loading/> :
                    <PostCreate data={this.props.accountsOwnSearch} refetch={this.props.refetch} onSubmit={this.savePosts} controls={{media:true, date:true,location:true,preview:true,discard:true,draft:true}}/>}
            </div>
        )
    }
}

const QUERY = gql`
    query($query: String!){
  accountsOwnSearch(query:$query){
    _id,
    network,
    information{
      name,
      lastName,
      avatar
    }
  }
}
`;

const withData = graphql(QUERY, {
    props: ({data: {error, loading, accountsOwnSearch, refetch}}) => {
        if (loading) return {loading: true};
        if (error) return {hasErrors: true};
        return {
            loading,
            accountsOwnSearch,
            refetch,
        };
    },
    options: (ownProps) => (
        {
            pollInterval: 150000,
            variables: { query: "" }
        }
    ),
});

const PostCreateContainerWithData = withData(PostCreateContainer);


export default PostCreateContainerWithData;
