import React from "react";
import {graphql} from "react-apollo";
import Loading from "../../../core/ui/components/loading";
import CampaignInvitesList from "../components/invites";
import {createContainer} from "meteor/react-meteor-data";
import gql from "graphql-tag";


class CampaignInvitesContainer extends React.Component {
    render() {
        let{invitesCampaign,teamAttacksOwn,accounts,campaign,refetch}=this.props;
        return (
            <div>
                {this.props.loading ? <Loading/> :
                    <CampaignInvitesList invites={invitesCampaign}
                                         accounts={accounts}
                                         teamAttacks={teamAttacksOwn}
                                         campaign={campaign}
                                         refetch={refetch}/>}
            </div>
        )
    }
}

const QUERY = gql`
query($campaignID: String!){
invitesCampaign(campaignID: $campaignID){
        _id,
        type,
        status,
        deadline,
        budget,
        useHashtag,
        invitesAvailable,
        networks{
            facebook,
            twitter,
            instagram
        }
        options{
            message,
            media,
            date,
            quantity
        },
        influencers {
            _id
            network
            information{
                name,
                lastName,
                avatar
            }
        },
        teamAttacks{
          name,
          _id,
          members{
             _id
            network
            information{
                name,
                lastName,
                avatar
            }
            pricing{
                post
                share
                partnership
                ambassador,
                profilePicture,
                coverPhoto,
                noPostHour,
                noPostDay,
            }
          }
        },
    }
    accounts{
        _id,
        network,
        information{
            name,
            lastName,
            avatar
        },
    }
    teamAttacksOwn{
        _id,
        owner {
            _id
        },
        members {
            _id,
            network,
            information{
                name,
                lastName,
                avatar,
                birthDate,
                country,
                gender
            }
            pricing{
                post
                share
                partnership
                ambassador,
                profilePicture,
                coverPhoto,
                noPostHour,
                noPostDay,
            }
        },
        name,
        type
    }
}
`;

const withData = graphql(QUERY, {
    props: ({data: {error, loading, invitesCampaign, teamAttacksOwn, accounts, refetch}}) => {
        if (loading) return {loading: true};
        if (error) return {hasErrors: true};
        return {
            loading,
            invitesCampaign,
            accounts,
            teamAttacksOwn,
            refetch,
        };
    },
    options: (ownProps) => ({
            pollInterval: 150000,
            variables: {
                "campaignID": ownProps.campaign._id
            }
        }
    ),
});

const CampaignInvitesContainerWithData = withData(CampaignInvitesContainer);


export default CampaignInvitesContainerWithData;
