import React from "react"
import { Translate, I18n } from "react-redux-i18n";
import { Accounts } from 'meteor/accounts-base'

class ProfilePassword extends React.Component {
    constructor(){
        super()
    }
    changePassword(){
        var oldPassword=$scope.passwordChange.old,
            newPassword=$scope.passwordChange.new;

        Accounts.changePassword(oldPassword, newPassword, function(err){
            if(err){
                notie.alert(3,"Password Incorrect, No changes were made.",3);
                return;
            }
            notie.alert(1,"Password Change Successful.",3);
            $scope.passwordChange={
                old:null,
                new:null,
                repeat:null
            };
        });
    }
    render(){
        let getTranslation = (key) => {
            return "Settings.tabs.profile.password." + key;
        };
        return(
            <div className="panel">
                <div className="panel-heading">
                    <h3 className="panel-title">
                        <Translate value={getTranslation("title")}/>
                    </h3>
                </div>
                <div className="panel-body container-fluid">
                    <div className="form-group">
                        <label htmlFor="informationName">
                            <Translate value={getTranslation("form.current")}/>
                        </label>
                        <input type="text" className="form-control"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="informationName">
                            <Translate value={getTranslation("form.new")}/>
                        </label>
                        <input type="text" className="form-control"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="informationName">
                            <Translate value={getTranslation("form.repeat")}/>
                        </label>
                        <input type="text" className="form-control"/>
                    </div>

                    <button type="button" className="btn btn-primary btn-block"><Translate value={getTranslation("form.button")}/></button>
                </div>
            </div>
        )
    }
}

export default ProfilePassword;
