import React from "react";
import Modal from "react-modal";
import { Translate } from "react-redux-i18n";
import ListEmpty from "../../../core/ui/components/list-empty";
import ListTable from "../components/list-table";
import CampaignTypes from "../components/campaign-types";
import CampaignCreateContainer from "../containers/campaign-create";


class List extends React.Component{
    constructor() {
        super();

        this.state = {
            modalIsOpen: false
        };

        this.openModal = this.openModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
    }

    openModal() {
        this.setState({modalIsOpen: true});
    }

    closeModal() {
        this.setState({modalIsOpen: false});
    }
    render() {
        let getTranslation=(key)=>{
            return "Campaigns."+key;
        };
        return (
            <div className="page-content container-fluid">
                <CampaignTypes/>
                {(this.props.data && this.props.data.length)?<ListTable data={this.props.data} new={this.openModal}/>:<ListEmpty message={<Translate value={getTranslation("empty.message")}/>} button={<Translate value={getTranslation("empty.button")}/>} callback={this.openModal}/>}
                <Modal
                    isOpen={this.state.modalIsOpen}
                    onRequestClose={this.closeModal}
                    className="app-modal"
                    contentLabel="Example Modal"
                >
                    <button className="close" onClick={this.closeModal}>
                        <i className="icon wb-close-mini" aria-hidden="true" />
                    </button>
                    <CampaignCreateContainer close={this.closeModal} reloadCampaigns={this.props.refetch}/>

                </Modal>
            </div>
        );
    }
}

export default List;
