import React from "react";
import {graphql} from "react-apollo";
import {connect} from "react-redux";
import Loading from "../../../core/ui/components/loading";
import ViewCampaign from "../layouts/view";
import {createContainer} from "meteor/react-meteor-data";
import gql from "graphql-tag";

let getTranslation=(key)=>{
    return "Campaigns.edit.messages."+key;
};

class ViewContainer extends React.Component {
    constructor() {
        super();

    }
    render() {
        let {invitesCampaign, refetch, history, campaign}=this.props;
        return (
            <div>
                {this.props.loading ? <Loading/> :
                    <ViewCampaign campaign={campaign}
                                  invitesCampaign={invitesCampaign}
                                  refetch={refetch}
                                  history={history} />}
            </div>
        )
    }
}

const QUERY = gql`
    query ($_id: String!){
        campaign(_id:$_id){
            _id,
            owner {
                _id,
                profile{
                    name,
                    lastName,
                    avatar
                }
            },
            manager {
                _id,
                profile{
                    name,
                    lastName,
                    avatar
                }
            },
            information {
                brands{
                    _id,
                    network,
                    information{
                        name,
                        lastName,
                        avatar
                    }
                }
                profile
                cover
                name
                conquer
                dateStart
                dateEnd
                description
                url
                objectives {
                    increaseFollowers,
                    increaseViews,
                    increaseShares,
                    increaseEngagement,
                    increaseLikes,
                    increaseTrafficHours,
                    reachNewMarkets,
                    increaseSpeaking,
                    increasePlays,
                    increaseWebsiteTraffic,
                    positionTarget,
                    keepTarget,
                    presence,
                    positionNewMarkets,
                },
            },
            messages {
                _id
                message
                hashtags
                media
            },
            gallery {
                images{
                    name,
                    content
                },
                videos{
                    name,
                    content
                }
            }
        }
        invitesCampaign(campaignID: $_id){
            _id,
            type,
            status,
            deadline,
            budget,
            useHashtag,
            invitesAvailable,
            networks{
                facebook,
                twitter,
                instagram
            }
            options{
                message,
                media,
                date,
                quantity
            },
            influencers {
                _id
                network
                information{
                    name,
                    lastName,
                    avatar
                }
            },
            teamAttack,
            teamAttackMembers{
                _id
                network
                information{
                    name,
                    lastName,
                    avatar
                }
                pricing{
                    post
                    share
                    partnership
                    ambassador,
                    profilePicture,
                    coverPhoto,
                    noPostHour,
                    noPostDay,
                }
            }
        }
    }
`;

const withData = graphql(QUERY, {
    props: ({data: {error, loading, campaign, invitesCampaign, refetch}}) => {
        //console.log("CAMPAIGN_ERROR",error,campaign);
        if (loading) return {loading: true};
        if (error) return {hasErrors: true};
        return {
            loading,
            campaign,
            invitesCampaign,
            refetch,
        };
    },
    options: (ownProps) => (
        {
            variables: {
                _id: ownProps.params.campaignID
            }
        }
    ),
});

const ViewContainerWithData = withData(ViewContainer);

export default connect(state => {
    return {
        form: state.form["campaign-edit"]
    }
})(ViewContainerWithData)
