import React from "react";
import BigCalendar from "react-big-calendar";
import { Translate,I18n } from "react-redux-i18n";
import {Tooltip, OverlayTrigger} from "react-bootstrap";
import SocialAvatar from "../../../core/ui/components/social-avatar";

function getTranslation(key){
    return "Agenda.calendar."+key;
};

function tooltipContent(content) {
    return <Tooltip id="tooltip_content">{content}</Tooltip>
}

function Event({ event }) {
    return (
    <OverlayTrigger placement="left"
                    overlay={tooltipContent(<Translate value={getTranslation("types."+event.type)}/>)}>
        <div className={"event-"+event.type}>
            {event.preview && <SocialAvatar name={event.title} network={event.network} avatar={event.preview} size="35"/>}
            <span>{event.title}</span>
        </div>
    </OverlayTrigger>
    )

}

function EventAgenda({ event }) {
    return (
        <div className={"event-"+event.type}>
            {event.preview && <SocialAvatar name={event.title} network={event.network} avatar={event.preview} size="35"/>}
            <span>{event.title} (<Translate value={getTranslation("types."+event.type)}/>)</span>
        </div>
    )
}

BigCalendar.momentLocalizer(moment); // or globalizeLocalizer

class Calendar extends React.Component {
    render() {
        let messages={
            allDay: <Translate value={getTranslation("labels.allDay")}/>,
            previous: <Translate value={getTranslation("labels.previous")}/>,
            next: <Translate value={getTranslation("labels.next")}/>,
            today: <Translate value={getTranslation("labels.today")}/>,
            month: <Translate value={getTranslation("labels.month")}/>,
            week: <Translate value={getTranslation("labels.week")}/>,
            day: <Translate value={getTranslation("labels.day")}/>,
            agenda: <Translate value={getTranslation("labels.agenda")}/>,
            date: I18n.t(getTranslation("labels.date")),
            time: I18n.t(getTranslation("labels.time")),
            event: I18n.t(getTranslation("labels.event"))
        };
        let formats = {
            dateFormat: (date, culture, localizer) => {
                return moment(date).format("DD");
            },
            monthHeaderFormat: (date, culture, localizer) => {
                let month=moment(date).format("MMMM").toLowerCase();
                return I18n.t(getTranslation("months."+month))+" "+moment(date).format("YYYY");
            },
            dayFormat: (date, culture, localizer) => {
                let day=moment(date).format("dddd").toLowerCase();
                return I18n.t(getTranslation("days."+day)).substr(0,2)+" "+moment(date).format("DD/MM");
            },

            weekdayFormat: (date, culture, localizer) => {
                let day=moment(date).format("dddd").toLowerCase();
                return I18n.t(getTranslation("days."+day));
            }
        };
        let events=this.props.events.map((e)=>Object.assign({},e,{
            start:new Date(e.start),
            end:new Date(e.end),
        }));
        return (
            <div>
                <BigCalendar
                    events={events}
                    defaultDate={new Date()}
                    defaultView='month'
                    timeslots={12}
                    popup={true}
                    messages={messages}
                    formats={formats}
                    components={{
                        event: Event,
                        agenda: {
                            event: EventAgenda
                        }
                    }}
                />
            </div>
        );
    }
}

export default Calendar;
