import React from "react";
import {Translate, I18n} from "react-redux-i18n";
import Switch from "react-switchery";
import {Field} from "redux-form";
import Actions from "./actions/actions";


class ActionForm extends React.Component {
    render() {
        let getTranslation = (key) => {
            return "Campaigns.edit.tabs.invites.edit." + key;
        };
        let {currentInvite, updateInviteValue, addInviteOption}=this.props;
        let disabled=currentInvite.options.length>0;
        return (
            <div className="panel">
                <div className="panel-body container-fluid">
                    <div className="row">
                        <div className="col-md-4">
                            <div className="form-group">
                                <label htmlFor="informationName">
                                    <Translate value={getTranslation("form.action.label")}/>
                                </label>
                                <select className="form-control" onChange={updateInviteValue("type")} value={currentInvite.type} disabled={disabled}>
                                    <option value="post">{I18n.t(getTranslation("form.action.values.post"))}</option>
                                    <option
                                        value="profile">{I18n.t(getTranslation("form.action.values.profilePicture"))}</option>
                                    <option
                                        value="cover">{I18n.t(getTranslation("form.action.values.coverPhoto"))}</option>
                                    <option
                                        value="hourNoPost">{I18n.t(getTranslation("form.action.values.noPost.hour"))}</option>
                                    <option
                                        value="dayNoPost">{I18n.t(getTranslation("form.action.values.noPost.day"))}</option>
                                    <option value="share">{I18n.t(getTranslation("form.action.values.share"))}</option>
                                </select>
                            </div>
                        </div>
                        <div className="col-md-4 col-md-offset-4">
                            <div className="checkbox-custom checkbox-primary pull-xs-right margin-right-20">
                                <input type="checkbox"
                                       id="inviteAll"
                                       onChange={(e)=>updateInviteValue("useHashtag")(e.target.checked)}
                                       checked={currentInvite.useHashtag}/>
                                <label htmlFor="inviteAll"><Translate value={getTranslation("form.useHashtag")}/></label>
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-md-12">
                            <Actions type={currentInvite.type} addInviteOption={addInviteOption}/>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default ActionForm;
