import React from "react";
import Select from "react-select";
import Gravatar from "react-gravatar";
import SocialAvatar from "../social-avatar"
import createReactClass from "create-react-class";
import PropTypes from 'prop-types';


const SocialAvatarOption = createReactClass({
    propTypes: {
        children: PropTypes.node,
        className: PropTypes.string,
        isDisabled: PropTypes.bool,
        isFocused: PropTypes.bool,
        isSelected: PropTypes.bool,
        onFocus: PropTypes.func,
        onSelect: PropTypes.func,
        option: PropTypes.object.isRequired,
    },
    handleMouseDown (event) {
        event.preventDefault();
        event.stopPropagation();
        this.props.onSelect(this.props.option, event);
    },
    handleMouseEnter (event) {
        this.props.onFocus(this.props.option, event);
    },
    handleMouseMove (event) {
        if (this.props.isFocused) return;
        this.props.onFocus(this.props.option, event);
    },
    render () {
        let {name, lastName,avatar}=this.props.option.information;
        return (
            <div className={this.props.className}
                 onMouseDown={this.handleMouseDown}
                 onMouseEnter={this.handleMouseEnter}
                 onMouseMove={this.handleMouseMove}
                 title={this.props.option.title}>
                <SocialAvatar avatar={avatar} name={name+" "+lastName} network={this.props.option.network} size="50"/>
                <span className="title">{name+" "+lastName}</span>
            </div>
        );
    }
});

const SocialAvatarValue = createReactClass({
    propTypes: {
        children: PropTypes.node,
        placeholder: PropTypes.string,
        value: PropTypes.object
    },
    render () {
        let {name, lastName,avatar}=this.props.value.information;
        return (
            <div className="Select-value" title={this.props.value.information.name}>
				<span className="Select-value-label">
					<SocialAvatar avatar={avatar} name={name+" "+lastName} network={this.props.value.network} size="50"/>
                    <span className="title">{name+" "+lastName}</span>
				</span>
            </div>
        );
    }
});


function arrowRenderer () {
    return (
        <span/>
    );
}

class AccountSearch extends React.Component {
    constructor(){
        super();

        this.setValue=this.setValue.bind(this);
        this.filterOptions=this.filterOptions.bind(this);

    }
    setValue (value) {
        this.props.input.onChange(value);

    }
    filterOptions(options,filter,current){
        return options.filter((o)=>{
            let {name,lastName}=o.information;
            if(current){
                let match=current.filter((c)=>c._id===o._id)[0];
                if(match){
                    return false;
                }
            }
            let regEx=new RegExp(filter.toLowerCase());
            return name.toLowerCase().match(regEx) || lastName.toLowerCase().match(regEx) || o.network.toLowerCase().match(regEx);
        });
    }
    render(){
        const {input: {value, onChange}, className} = this.props;
        return (
            <div>
                <Select
                    arrowRenderer={arrowRenderer}
                    onChange={onChange}
                    filterOptions={this.filterOptions}
                    multi={this.props.multi}
                    className={"social-select "+(className?className:"")}
                    optionComponent={SocialAvatarOption}
                    options={this.props.data}
                    placeholder={this.props.placeholder||""}
                    value={value}
                    valueComponent={SocialAvatarValue}
                />
            </div>
        )
    }
}

export default AccountSearch;
