import React from "react";
import Calendar from "../components/calendar";
import CalendarFilter from "../components/calendar-filter";
import CalendarMenu from "../components/menu";
import PostCreate from "../containers/post-create";
import Modal from "react-modal";
import { Translate } from "react-redux-i18n";

class Agenda extends React.Component {
    constructor() {
        super();

        this.state = {
            modalIsOpen: false
        };

        this.openModal = this.openModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
    }

    openModal() {
        this.setState({modalIsOpen: true});
    }

    closeModal() {
        this.setState({modalIsOpen: false});
    }
    render() {
        let getTranslation=(key)=>{
            return "Agenda."+key;
        };
        let events=this.props.events||[];
        let {accounts}=this.props;
        return (
        <div>
            <div className="page-content container-fluid">
                <div className="row">
                    <div className="col-md-3">
                        <button className="btn btn-primary" onClick={this.openModal}>
                            <Translate value={getTranslation("publish.button")}/>
                        </button>
                        <div className="spacer-30"></div>
                        <CalendarFilter filters={this.props.filters} update={this.props.update} accounts={accounts}/>
                    </div>
                    <div className="col-md-9">
                        <Calendar events={events}/>
                    </div>
                </div>
            </div>
            <Modal
                isOpen={this.state.modalIsOpen}
                onRequestClose={this.closeModal}
                className="app-modal"
                contentLabel="Example Modal"
            >
                <button className="close" onClick={this.closeModal}>
                    <i className="icon wb-close-mini" aria-hidden="true" />
                </button>
                <PostCreate close={this.closeModal} reloadAgenda={this.props.refetch}/>
            </Modal>
        </div>
        );
    }
}

export default Agenda;
