import React from "react";
import {Translate, I18n} from "react-redux-i18n";
import {BootstrapTable, TableHeaderColumn}  from "react-bootstrap-table";
import {Link} from "react-router";
import SocialAvatar from "../../../core/ui/components/social-avatar";

let getTranslation=(key)=>{
    return "Reporter.list."+key;
};

const renderLink = function(cell, row) {   // String example
    return <Link to={"/reporter/edit/"+cell}><i className="icon wb-edit" aria-hidden="true"/></Link>;
};

class ListTable extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            sortName: undefined,
            sortOrder: undefined
        };

        this.onSortChange = this.onSortChange.bind(this);
    }
    onSortChange(sortName, sortOrder) {
        this.setState({
            sortName,
            sortOrder
        });
    }
    render() {

        const options = {
            sortName: this.state.sortName,
            sortOrder: this.state.sortOrder,
            onSortChange: this.onSortChange,
            noDataText: I18n.t(getTranslation("table.empty"))
        };
        const list = this.props.data;
        return (
            <div>
                <div className="row">
                    <div className="col-md-12">
                        <button className="btn btn-primary table-button" onClick={this.props.new}>
                            <Translate value="Reporter.list.new"/>
                        </button>
                    </div>
                </div>
                <div className="ibol-table">
                    <BootstrapTable data={list} options={ options } pagination search searchPlaceholder={I18n.t(getTranslation("table.search"))}>
                        <TableHeaderColumn isKey dataField='_id' dataFormat={ renderLink } dataAlign='center' width="110px"><Translate value={getTranslation("table.headers.edit")}/></TableHeaderColumn>
                        <TableHeaderColumn dataField='name'  dataSort={ true }><Translate value={getTranslation("table.headers.name")}/></TableHeaderColumn>
                    </BootstrapTable>
                </div>

            </div>
        )
    }
}

export default ListTable;
