import React from "react";
import {connect} from "react-redux";
import PostTimeDate from "./post-create/time-date";
import PostPreview from "./post-create/preview";
import PostLocation from "./post-create/location";
import PostCreateControls from "./post-create/controls";

import {Translate, I18n} from "react-redux-i18n";
import {Field, reduxForm} from "redux-form";
import store from "../../../../store";
import AccountSelect from "./post-create/account-select";
import InputTextarea from "./post-create/input-textarea";
import InputTags from "../../../core/ui/components/forms/input-tags";

class PostCreate extends React.Component {
    constructor() {
        super();

        this.state = {
            ui: {
                timeDate: false,
                preview: false,
                location: false
            },
            image:null
        };
        this.store = store;
        this.openUIModal = this.openUIModal.bind(this);
        this.closeUIModal = this.closeUIModal.bind(this);
        this.handleTags =  this.handleTags.bind(this);
        this.setImage=this.setImage.bind(this)
    }

    handleTags(tags) {
        const validatedTags=tags.map((tag)=>tag.split("#").length>1?tag:"#"+tag);

        if(validatedTags.indexOf("#ibol")===-1){
            validatedTags.push("#ibol");
        }
        return validatedTags;
    }

    setImage(image){
        this.setState({image});
    }

    openUIModal(modal) {
        let component=this;
        return function () {
            let newState = component.state;
            newState.ui[modal] = true;
            component.setState(newState)
        }
    }

    closeUIModal(modal) {
        return function () {
            let newState = this.state;
            newState.ui[modal] = false;
            this.setState(newState)
        }
    }
    componentWillMount(){
        if(this.props.initialValues && this.props.initialValues.media){
            this.setState({image:this.props.initialValues.media});
        }
    }
    render() {
        const { handleSubmit, pristine, reset, submitting, controls, data, refetch } = this.props;
        let getTranslation = (key) => {
            return "Agenda.publish.modal." + key;
        };
        //let values= this.props.form.values;
        return (
            <form className="post-create" onSubmit={handleSubmit}>
                <div className="row">
                    <div className={data?"col-md-8":"col-md-12"}>
                        <div className="panel">

                            <div className="panel-body container-fluid padding-20">
                                <div className="post-write">
                                    <div className={"image "+(this.state.image?"on":"")}>
                                        {this.state.image && <img src={this.state.image} alt=""/>}
                                    </div>
                                    <Field component={InputTextarea} name="message"
                                           placeholder={I18n.t(getTranslation('write.placeholder'))}/>
                                    <div className="row">
                                        <div className="col-md-8">
                                            <PostCreateControls setImage={this.setImage}
                                                                openUIModal={this.openUIModal}
                                                                settings={controls}/>

                                        </div>
                                        <div className="col-md-4">
                                            <button type="submit" className="btn btn-primary pull-right">
                                                <Translate value={getTranslation("write.controls.schedule")}/>
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    {data && (
                        <div className="col-md-4">
                            <Field name="accounts" component={AccountSelect} placeholder={I18n.t(getTranslation('write.search'))}
                                   data={data}
                                   refetch={refetch}/>
                        </div>
                    )}

                </div>



                <Field name="date" component={PostTimeDate}
                       open={this.state.ui.timeDate}
                       close={this.closeUIModal("timeDate").bind(this)} store={this.store}/>

                <Field name="location" component={PostLocation}
                       open={this.state.ui.location}
                       close={this.closeUIModal("location").bind(this)} store={this.store}/>

                <PostPreview open={this.state.ui.preview} closeButton={<Translate value={getTranslation("write.close")}/>} close={this.closeUIModal("preview").bind(this)}/>
            </form>
        );
    }
}

PostCreate = reduxForm({
    form: 'post-create'
})(PostCreate);

export default PostCreate;
