import React from "react";
import { Tabs, Tab } from "react-bootstrap";
import { Translate } from "react-redux-i18n";
import {FormSection, Field} from "redux-form";
import CampaignCalendar from "../containers/campaign-calendar";
import Gallery from "../components/gallery";
import Information from "../components/information";
import Status from "../components/status";
import Settings from "../components/settings";
import Summary from "../components/summary";
import CampaignInvites from "../containers/campaign-invites";


class TabsContainer extends React.Component {
    render() {
        const { campaign, refetch, invitesCampaign, teamAttacksOwn, accounts } = this.props;
        return (
            <div>
                <div className="nav-tabs-horizontal">
                    <Tabs defaultActiveKey="summary" id="uncontrolled-tab-example" bsStyle="tabs">
                        <Tab eventKey="summary" title={<Translate value="Campaigns.edit.tabs.summary.title"/>}>
                            <Summary campaign={campaign}
                                     invites={invitesCampaign}/>
                        </Tab>
                        <Tab eventKey="settings" title={<Translate value="Campaigns.edit.tabs.settings.title"/>}>
                            <Settings campaign={campaign} userId={this.props.userId}  ownAccounts={this.props.ownAccounts}/>
                        </Tab>
                        <Tab eventKey="information" title={<Translate value="Campaigns.edit.tabs.information.title"/>}>
                            <FormSection name="information">
                                <Information campaign={campaign}  ownAccounts={this.props.ownAccounts} owner={this.props.campaign.owner}/>
                            </FormSection>
                        </Tab>
                        <Tab eventKey="status" title={<Translate value="Campaigns.edit.tabs.status.title"/>}>
                            <Status/>
                        </Tab>
                        <Tab eventKey="invites" title={<Translate value="Campaigns.edit.tabs.invites.title"/>}>
                            <CampaignInvites campaign={campaign}/>
                        </Tab>
                        <Tab eventKey="calendar" title={<Translate value="Campaigns.edit.tabs.calendar.title"/>}>
                            <CampaignCalendar campaignID={this.props.campaignID} controls={true}/>
                        </Tab>
                        <Tab eventKey="gallery" title={<Translate value="Campaigns.edit.tabs.gallery.title"/>}>
                            <FormSection name="gallery">
                                <Gallery/>
                            </FormSection>
                        </Tab>
                    </Tabs>
                </div>
            </div>
        );
    }
}

export default TabsContainer;
