import React from "react";
import ActionPost from "./post";
import ActionFileUpload from "./file-upload";
import ActionShare from "./share";
import ActionTimeWithout from "./time-without";
import PostTimeDate from "../../../../../../../agenda/ui/components/post-create/time-date";
import PostLocation from "../../../../../../../agenda/ui/components/post-create/location";
import  InputFileUpload from "../../../../../../../core/ui/components/forms/input-file-upload";
import {Translate} from "react-redux-i18n";

class Actions extends React.Component {
    constructor(){
        super();

        this.state={
            view: "action",
            action: {
                message: "",
                media: null,
                date: null,
                quantity: null,
                location: null
            }
        };

        this.toggleTime=this.toggleTime.bind(this);
        this.toggleLocation=this.toggleLocation.bind(this);
        this.updateActionValue=this.updateActionValue.bind(this);
        this.saveOption=this.saveOption.bind(this);
    }
    updateActionValue(key){
        let component=this;
        return function(e){
            let val=e;
            if(e.target){
                val=e.target.value;
            }
            let newState={...component.state};
            newState.action[key]=val;
            component.setState(newState);
        }
    }
    toggleTime(){
        let nextView=this.state.view==="action"?"date":"action";
        //console.log("TOGGLE_TIME",this.state.view,"=>",nextView);

        let component=this;
        setTimeout(function(){
            component.setState({
                view: nextView
            });
        },50);
    }
    toggleLocation(){
        let nextView=this.state.view==="action"?"location":"action";
        //console.log("TOGGLE_TIME",this.state.view,"=>",nextView);

        let component=this;
        setTimeout(function(){
            component.setState({
                view: nextView
            });
        },50);
    }
    getView(type, action, updateActionValue){
        switch(type) {
            case "post":
                return <ActionPost updateActionValue={updateActionValue} action={action}/>;
            case "profile":
                return <ActionFileUpload updateActionValue={updateActionValue} action={action}/>;
            case "cover":
                return <ActionFileUpload updateActionValue={updateActionValue} action={action}/>;
            case "hourNoPost":
                return <ActionTimeWithout updateActionValue={updateActionValue} action={action} type={type}/>;
            case "dayNoPost":
                return <ActionTimeWithout updateActionValue={updateActionValue} action={action} type={type}/>;
            case "share":
                return <ActionShare updateActionValue={updateActionValue} action={action}/>
        }
    }
    saveOption(){
        this.props.addInviteOption(this.state.action);
        this.setState({action: {
            message: "",
            media: null,
            date: null,
            quantity: null
        }});
    }
    render() {
        let getTranslation = (key) => {
            return "Campaigns.edit.tabs.invites.edit.form.action." + key;
        };
        let {type}=this.props;
        let {action}=this.state;
        let hasImage=type==="post";
        return (
            <div className="action-form">
                {this.state.view==="action" && (
                    <div>
                        {this.getView(type, action, this.updateActionValue)}
                        <div className="row" style={{position: "inherit"}}>
                            <div className="col-md-9" style={{position: "inherit"}}>
                                <ul className="controls">
                                    {hasImage && (
                                        <li>
                                            <InputFileUpload content={<i className="icon fa-photo" aria-hidden="true"/>}
                                                             settings={{multiple: false, maxSize:3 * 1024 * 1024, className:"icon-drop-zone",activeClassName:"icon-drop-zone-active", accept:"image/png, image/jpeg, image/gif"}}
                                                             uploader="campaign-image-upload"
                                                             callback={()=>{}} input={{onChange:this.updateActionValue("media"), value: null}}/>
                                        </li>
                                    )}
                                    <li>
                                        <i className="icon fa-calendar" aria-hidden="true" onClick={this.toggleTime}/>
                                    </li>
                                    <li>
                                        <i className="icon fa-location-arrow" aria-hidden="true" onClick={this.toggleLocation}/>
                                    </li>
                                </ul>
                            </div>
                            <div className="col-md-3">
                                <a className="btn btn-default pull-right" onClick={this.saveOption}><Translate value={getTranslation("save")}/></a>
                            </div>
                        </div>
                    </div>
                )}



                {this.state.view==="date" && (
                    <PostTimeDate input={{onChange:this.updateActionValue("date"),value:new Date()}}
                                  open={this.state.view}
                                  close={this.toggleTime}/>
                )}
                {this.state.view==="location" && (
                    <PostLocation input={{onChange:this.updateActionValue("location"),value:""}}
                                  open={this.state.view}
                                  close={this.toggleLocation}/>
                )}

            </div>
        )
    }
}


export default Actions;
